mapper.select:
SELECT user_follow_id,user_id,follow_id,follow_time
FROM tb_user_follow
WHERE
user_id=#{userId}
AND
follow_id=#{followId}

SELECT count(user_follow_id)
FROM tb_user_follow
WHERE
user_id=#{userId}
AND
follow_id=#{followId}

mapper.delete:
DELETE FROM
tb_user_follow
WHERE
user_id=#{userId}
AND
follow_id=#{followId}
